select.where:

del_flag = '0'  
 and controller_name like concat('%', #{controllerName}, '%')
 and ip = #{ip}
 and type = #{type}
 and status = #{status}

mapper.update:
update acs_device_controller
controller_name = #{controllerName}, ip = #{ip}, port = #{port}, type = #{type}, capacity = #{capacity}, user_name = #{userName}, password = #{password}, status = #{status}, alarm_status = #{alarmStatus}, identifier = #{identifier}, controller_index = #{controllerIndex}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where controller_id = #{controllerId}

mapper.select:
select distinct dc.controller_id, dc.controller_name, dc.ip, dc.port, dc.type, dc.capacity, dc.user_name, dc.password, dc.status, dc.alarm_status, dc.identifier, dc.controller_index, dc.del_flag, dc.create_by, dc.create_time, dc.update_by, dc.update_time, dc.remark from acs_device_controller as dc
LEFT JOIN acs_device_zone dz on dc.controller_id IN (SELECT controller_id FROM acs_device_zone_controller WHERE zone_id = dz.zone_id )
LEFT JOIN sys_dept d on dz.dept_id = d.dept_id

    dc.del_flag = '0'  
     and dc.controller_name like concat('%', #{controllerName}, '%')
     and dc.ip = #{ip}
     and dc.type = #{type}
     and dc.status = #{status}
    and dz.del_flag = '0'
    and dz.status = '0'


${params.dataScope}
