select.where:

del_flag = '0'
 and door_name like concat('%', #{doorName}, '%')
 and controller_name like concat('%', #{controllerName}, '%')
 and pin = #{pin}
 and status = #{status}
and controller_id = #{controllerId}

mapper.update:
update acs_device_door
door_name = #{doorName}, controller_id = #{controllerId}, controller_name = #{controllerName}, pin = #{pin}, status = #{status}, identifier = #{identifier}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where door_id = #{doorId}

mapper.select:
select a.door_id, a.door_name, a.controller_id, a.controller_name, a.pin, a.status, a.identifier, a.del_flag, a.create_by, a.create_time, a.update_by, a.update_time, a.remark from acs_device_door a left join acs_device_controller b on a.controller_id=b.controller_id left join acs_device_zone_controller c on c.controller_id=b.controller_id left join acs_device_zone ad on c.zone_id=ad.zone_id left join sys_dept d on ad.dept_id=d.dept_id
a.del_flag = '0' and not exists (select 1 from asc_door_group_door b where b.door_id=a.door_id and b.door_group_id=#{groupId}) and a.door_name like concat('%', #{doorName}, '%') and a.controller_name like concat('%', #{controllerName}, '%') and a.pin = #{pin} and a.status = #{status} and ad.del_flag = '0' and ad.status = '0'
${params.dataScope}

select distinct dd.door_id, dd.door_name, dd.controller_id, dd.controller_name, dd.pin, dd.status, dd.identifier, dd.del_flag, dd.create_by, dd.create_time, dd.update_by, dd.update_time, dd.remark from acs_device_door as dd
LEFT JOIN acs_device_controller dc ON dd.controller_id = dc.controller_id
LEFT JOIN acs_device_zone dz on dc.controller_id IN (SELECT controller_id FROM acs_device_zone_controller WHERE zone_id = dz.zone_id )
LEFT JOIN sys_dept d on dz.dept_id = d.dept_id

    dd.del_flag = '0'
     and dd.door_name like concat('%', #{doorName}, '%')
     and dd.controller_name like concat('%', #{controllerName}, '%')
     and dd.pin = #{pin}
     and dd.status = #{status}
    and dd.controller_id = #{controllerId}
    and dz.del_flag = '0'
    and dz.status = '0'


${params.dataScope}
