mapper.select:


    and 1=1
     and a.group_name like concat('%', #{groupName}, '%')
     and a.status = #{status}


${params.dataScope}

select a.group_id, a.group_name, a.dept_id, d.dept_name, a.status, a.del_flag, a.create_by, a.create_time, a.update_by, a.update_time from asc_user_group a left join sys_dept d on a.dept_id=d.dept_id
and 1=1 and not exists (select * from asc_door_group_user_group b where a.group_id=b.user_group_id and b.door_group_id=#{doorGroupId}) and a.group_name like concat('%', #{groupName}, '%') and a.status = '0' and a.del_flag = '0'
${params.dataScope}

select a.group_id, a.group_name, a.dept_id, d.dept_name, a.status, a.del_flag, a.create_by, a.create_time, a.update_by, a.update_time from asc_user_group a left join sys_dept d on a.dept_id=d.dept_id
and 1=1 and exists (select * from asc_door_group_user_group b where a.group_id=b.user_group_id and b.door_group_id=#{doorGroupId}) and a.group_name like concat('%', #{groupName}, '%') and a.status = '0' and a.del_flag = '0'
${params.dataScope}

mapper.update:
update asc_user_group
group_name = #{groupName}, status = #{status}, dept_id = #{deptId}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where group_id = #{groupId}
